import React, { useState } from "react";
import axios from "axios";

function TodoItem({ todo, refresh }) {
const [editing, setEditing] = useState(false);
const [title, setTitle] = useState(todo.title);
const [description, setDescription] = useState(todo.description || "");
const [saving, setSaving] = useState(false);

const toggle = async () => {
try {
await axios.patch(
(process.env.REACT_APP_API_BASE || "[http://localhost:5000](http://localhost:5000)") + `/api/todos/${todo._id}/done`
);
refresh();
} catch (err) {
console.error(err);
}
};

const save = async () => {
if (!title.trim() || !description.trim()) {
alert("Title and description are required.");
return;
}
setSaving(true);
try {
await axios.put(
(process.env.REACT_APP_API_BASE || "[http://localhost:5000](http://localhost:5000)") + `/api/todos/${todo._id}`,
{ title, description }
);
setEditing(false);
refresh();
} catch (err) {
console.error(err);
} finally {
setSaving(false);
}
};

const remove = async () => {
if (!window.confirm("Delete this todo?")) return;
try {
await axios.delete(
(process.env.REACT_APP_API_BASE || "[http://localhost:5000](http://localhost:5000)") + `/api/todos/${todo._id}`
);
refresh();
} catch (err) {
console.error(err);
}
};

return (
<div
style={{
marginBottom: 12,
padding: 12,
borderRadius: 8,
border: "1px solid #eee",
display: "flex",
alignItems: "center",
gap: 12,
backgroundColor: todo.done ? "#f0fff0" : "#fff",
transition: "background-color 0.3s",
}}
>
{/* Toggle Switch */}
<label style={{ position: "relative", display: "inline-block", width: 44, height: 24 }}>
<input
type="checkbox"
checked={todo.done}
onChange={toggle}
style={{ opacity: 0, width: 0, height: 0 }}
/>
<span
style={{
position: "absolute",
cursor: "pointer",
top: 0,
left: 0,
right: 0,
bottom: 0,
backgroundColor: todo.done ? "#4caf50" : "#ccc",
borderRadius: 24,
transition: "0.4s",
}}
/>
<span
style={{
position: "absolute",
left: todo.done ? 20 : 2,
top: 2,
width: 20,
height: 20,
backgroundColor: "#fff",
borderRadius: "50%",
transition: "0.4s",
}}
/> </label>

```
  <div style={{ flex: 1 }}>
    {editing ? (
      <div style={{ display: "flex", flexDirection: "column", gap: 6 }}>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          style={{ padding: 6, borderRadius: 4, border: "1px solid #ccc" }}
        />
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          style={{ padding: 6, borderRadius: 4, border: "1px solid #ccc" }}
        />
      </div>
    ) : (
      <div>
        <div
          style={{
            fontWeight: "bold",
            fontSize: 16,
            textDecoration: todo.done ? "line-through" : "none",
            color: todo.done ? "#999" : "#000",
          }}
        >
          {todo.title}
        </div>
        {description && (
          <div
            style={{
              fontSize: 13,
              color: todo.done ? "#999" : "#555",
              marginTop: 2,
            }}
          >
            {todo.description}
          </div>
        )}
      </div>
    )}
  </div>

  <div style={{ display: "flex", gap: 6 }}>
    {editing ? (
      <>
        <button onClick={save} disabled={saving} style={{ padding: "6px 12px" }}>
          {saving ? "Saving..." : "Save"}
        </button>
        <button onClick={() => setEditing(false)} style={{ padding: "6px 12px" }}>
          Cancel
        </button>
      </>
    ) : (
      <>
        <button onClick={() => setEditing(true)} style={{ padding: "6px 12px" }}>
          Edit
        </button>
        <button onClick={remove} style={{ padding: "6px 12px", color: "#f44336" }}>
          Delete
        </button>
      </>
    )}
  </div>
</div>
