import React, { useState } from "react";
import axios from "axios";

function TodoItem({ todo, refresh }) {
  const [editing, setEditing] = useState(false);
  const [title, setTitle] = useState(todo.title);
  const [description, setDescription] = useState(todo.description || "");
  const [saving, setSaving] = useState(false);

  const toggle = async () => {
    await axios.patch((process.env.REACT_APP_API_BASE || "http://localhost:5000") + `/api/todos/${todo._id}/done`);
    refresh();
  };

  const save = async () => {
    setSaving(true);
    try {
      await axios.put((process.env.REACT_APP_API_BASE || "http://localhost:5000") + `/api/todos/${todo._id}`, { title, description });
      setEditing(false);
      refresh();
    } catch (err) {
      // ignore for demo
    } finally { setSaving(false); }
  };

  const remove = async () => {
    if (!window.confirm("Delete this todo?")) return;
    await axios.delete((process.env.REACT_APP_API_BASE || "http://localhost:5000") + `/api/todos/${todo._id}`);
    refresh();
  };

  return (
    <div style={{ marginBottom: 12, padding: 10, border: "1px solid #eee", borderRadius: 6, display: "flex", gap: 8, alignItems: "center" }}>
      <input type="checkbox" checked={todo.done} onChange={toggle} />
      <div style={{ flex: 1 }}>
        {editing ? (
          <>
            <input value={title} onChange={e => setTitle(e.target.value)} />
            <input value={description} onChange={e => setDescription(e.target.value)} />
          </>
        ) : (
          <div style={{ textDecoration: todo.done ? "line-through" : "none" }}>
            <strong>{todo.title}</strong>
            <div style={{ fontSize: 13, color: "#555" }}>{todo.description}</div>
          </div>
        )}
      </div>
      {editing ? (
        <>
          <button onClick={save} disabled={saving}>{saving ? "Saving..." : "Save"}</button>
          <button onClick={() => setEditing(false)}>Cancel</button>
        </>
      ) : (
        <>
          <button onClick={() => setEditing(true)}>Edit</button>
          <button onClick={remove}>Delete</button>
        </>
      )}
    </div>
  );
}

export default TodoItem;
